INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('98be0b50-c9b7-4253-a6ea-b5be4c067122', 'Career and Growth Club', 'Raum KEP101', '2025-10-27', '17:00 – 18:30', 'https://images.unsplash.com/photo-1522071820081-009f0129c71c?w=800&q=80', 'Networking', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Coming to WHZ is the start of a new chapter — but it can also feel confusing and overwhelming. The Career & Growth Club is here to make sure you don''t lose track of your goals. It''s a student-led space where seniors share real experiences, juniors find guidance, and everyone supports each other in building a stronger academic and career path. What we do: peer-to-peer mentoring, monthly meetups, practical tips, guest inputs and ongoing support.', 'English', 'Register on OPAL to participate in this student networking event.');
