INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('b76ef6b1-2b80-416c-b21d-e0b60f636bd3', 'Exkursion: CONTINENTAL AEROSPACE TECHNOLOGIES GmbH', 'Platanenstr. 14, 09356 St. Egidien', '2025-10-27', '11:00 – 12:30', 'https://images.unsplash.com/photo-1581091226825-a6a2a5aee158?w=800&q=80', 'Excursion', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Die Continental Aerospace Technologies GmbH ist ein mittelständischer Systempartner der Luftfahrt. Das Unternehmen konzipiert, entwickelt und produziert mechanische sowie elektronische Komponenten für Hochleistungsmotoren. Bekannt ist es insbesondere für Dieselkolbenflugmotoren, die in Kleinflugzeugen der Allgemeinen Luftfahrt eingesetzt werden.', 'Deutsch', 'Mit Ihrer Anmeldung stimmen Sie zu, dass Ihr Vor- und Nachname sowie Geburtsdatum an das Unternehmen weitergegeben wird. Bitte übermitteln Sie nach Ihrer Anmeldung bis spätestens den 20.10.2025 Ihr Geburtsdatum an anais.kelly@whz.de. Treffpunkt am Zwickauer Hauptbahnhof. Anmeldung über OPAL. Kurzfristige Abmeldung über OPAL bis zum 20.10 verpflichtend.');
