INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('d478d905-85d8-433c-b67e-c0cf9d17ed9d', 'KarriereSCHUBkarre', 'Aula Scheffelstraße', '2025-10-29', 'ab 14:00', 'https://images.unsplash.com/photo-1511578314322-379afb476865?w=800&q=80', 'Career Fair', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Die Veranstaltung KarriereSCHUBkarre bringt Studierende, Hochschulen und Unternehmen an einen Tisch, um Zukunftsthemen gemeinsam zu bewegen. Ziel der Veranstaltung ist es, Wege zu entwickeln, wie studentische Wünsche stärker berücksichtigt und praxisnahe Chancen für Praktika, Abschlussarbeiten und Berufseinstiege geschaffen werden können. The open exchange creates space for new ideas, mutual understanding and sustainable networks.', 'Deutsch & English', 'Zielgruppe: Studierende der Fakultät Kraftfahrzeugtechnik. Messeveranstaltung mit Unternehmen und Industriepartnern.');
INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('df479420-89e4-4afd-acda-d79e2ffcb8b1', 'Career Service Office Hours', 'Campus Scheffelstraße, S_6102', '2025-10-29', '13:00 – 16:00', 'https://images.unsplash.com/photo-1573497019940-1c28c88b4f3e?w=800&q=80', 'Counseling', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Der Career Service hilft euch bei Fragen zu Bewerbungsunterlagen, Praktika und Werkstudententätigkeiten. Außerdem unterstützen wir dich beim Besuch von Job- und Fachmessen, bei der Wahl von Studienschwerpunkten und der Suche nach Stipendien. Auch bei Fragen zu Arbeitszeugnissen, Weiterbildung und Perspektivenwechsel nach dem Studium stehen wir dir zur Seite.', 'Deutsch & English', 'Drop-in office hours. No registration required. Referentin: Frau Melanie Braun, WHZ.');
INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('e4725f91-d2e5-42b6-80d4-59c2c3bc2620', 'JungIngenieureNetzwerk – Wegziehen oder bleiben?', 'Volkswagen Bildungsinstitut, Reichenbacher Str. 76, 08056 Zwickau', '2025-10-29', '17:00 – 20:00', 'https://images.unsplash.com/photo-1552664730-d307ca884978?w=800&q=80', 'Networking', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Im JIN-Netzwerktreffen wollen wir mit Euch über die Frage „Wegziehen oder bleiben?" diskutieren. Unsere Automobilregion wird sich verändern. Die Richtung ist ungewiss. Optionen liegen auf dem Tisch. Ideen sind gefragt. Wandelfähigkeit wird über den Wohlstand bestimmen. Agenda: ITAS Präsentation, Masterplan Südwestsachsen, Blick in die Zukunft, Führung VW BI, Netzwerkzeit und Imbiss.', 'Deutsch', 'Shuttle-Transfer vom Campus Scheffelberg nach der KarriereSCHUBkarre. Networking event für JungIngenieure.');
INSERT INTO events (id, title, location, event_date, event_time, image_url, category, created_at, created_by, likes, prosts, description, language, registration_info) VALUES ('fa0e4d1c-983e-4441-bed1-b2fc47f1227c', 'CV Secrets – Go Beyond the Basics', 'City Center GAB247', '2025-10-28', '17:00 – 18:00', 'https://images.unsplash.com/photo-1454165804606-c3d57bc86b40?w=800&q=80', 'Workshop', '2025-10-24T18:55:39.548056+00:00', NULL, 0, 0, 'Still using the Europass format? Then you''re missing out on the chance to make your CV truly personal. In this workshop, you will learn how to move beyond generic templates — with clarity, authenticity, and strategy. You''ll also get a behind-the-scenes look at how HR professionals use AI tools to screen applications, and what that means for your CV. Learn how to stand out in a digital-first selection process.', 'English', 'Open workshop. Speaker: Gina Flachsbart, Career Service WHZ.');
