INSERT INTO whz_news (id, title, description, created_at) VALUES ('22b10cb1-9354-4c95-98bf-09a62e614074', 'WHZ-Forscher stellen kühlbaren Pavillon vor', 'Der Pavillon soll im Sommer nicht nur Schatten spenden, sondern auch die Luft darunter kühlen. Ein erster Prototyp konnte Anfang September der Ostdeutschen Motorrad-Trial-Meisterschaft 2025 in Oelsnitz/V. getestet werden.', '2025-10-24T20:53:57.311821+00:00');
INSERT INTO whz_news (id, title, description, created_at) VALUES ('6f1799e6-58b9-46cd-bfbe-ecf7214efa52', 'FerienUni 2025 bringt Schüler und Studierende zusammen', 'Unter dem Motto „Studieren checken, Campus entdecken" fand vom 13. bis 17. Oktober 2025 an der WHZ die diesjährige FerienUni statt.', '2025-10-24T20:53:57.311821+00:00');
INSERT INTO whz_news (id, title, description, created_at) VALUES ('904102a7-f6ac-4f57-bcaa-6aee986e5a97', 'FTZ gestaltet erneut GMM-Fachtagung „EMV in der Kraftfahrzeugtechnik"', 'Unter dem Motto „Herausforderungen und Lösungen zur Sicherstellung der EMV in modernen Automobilen" fand am 8. und 9. Oktober 2025 im Forschungs- und Innovationszentrum (FIZ) der BMW Group in München die 9. GMM-Fachtagung „Elektromagnetische Verträglichkeit (EMV) in der Kfz-Technik" statt.', '2025-10-24T20:53:57.311821+00:00');
INSERT INTO whz_news (id, title, description, created_at) VALUES ('aa7b82fa-8c8d-49ae-af7d-7e749f8280df', 'Forschungsforum der WHZ beleuchtet Facetten der Mobilität', 'Am 6. November findet das Forschungsforum der WHZ statt. Wissenschaftler der Hochschule zeigen dabei, dass Mobilität mehr ist als ein reines Verkehrsthema.', '2025-10-24T20:53:57.311821+00:00');
INSERT INTO whz_news (id, title, description, created_at) VALUES ('dec5b6cd-719b-4d11-987f-a80dcd38d052', 'Maschinenbau-Studierende erfolgreich mit regionalen Firmen vernetzt', 'Nur wenige Wochen nach Beginn des Wintersemesters zeigten sich die Studierenden neugierig auf neue Herausforderungen und möchten frühzeitig wichtige Kontakte für ihre berufliche Zukunft knüpfen.', '2025-10-24T20:53:57.311821+00:00');
INSERT INTO whz_news (id, title, description, created_at) VALUES ('e1db5400-727b-47a2-bf83-801ef30e7760', 'Aktionstag klärt über Demenz auf und gibt Tipps für den Alltag', 'Wie fühlt es sich an, wenn sich die eigene Wahrnehmung verändert? Dieser Frage widmete sich der Aktionstag „Demenz und Sinne", den die Lokale Allianz für Menschen mit Demenz Zwickau am 24. September im Innenhof des Schloss Osterstein veranstaltet wurde.', '2025-10-24T20:53:57.311821+00:00');
